    public int numDistinctIslands(int[][] grid) {
        if (grid == null || grid.length == 0 || grid[0].length == 0)
            return 0;

        Set<Integer> pattern = new HashSet<>();

        for (int r = 0; r < grid.length; r++) {
            for (int c = 0; c < grid[0].length; c++) {
                if (grid[r][c] == 1) {
                    pattern.add(bfs(grid, r, c));
                }
            }
        }
        return pattern.size();
    }

    private int bfs(int[][] grid, int r, int c) {
        Queue<int[]> q = new LinkedList<>();

        int level = 0;
        q.offer(new int[]{r, c});
        grid[r][c] = 0;
        StringBuilder path = new StringBuilder();

        while (!q.isEmpty()) {

            int size = q.size();

            for (int i = 1; i <= size; i++) {
                int[] poll = q.poll();
                int x = poll[0];
                int y = poll[1];

                path.append(level).append("S");

                if (x - 1 >= 0 && grid[x - 1][y] == 1) {
                    grid[x - 1][y] = 0;
                    q.offer(new int[]{x - 1, y});
                    path.append(level+1).append("U");
                }

                if (x + 1 <= grid.length - 1 && grid[x + 1][y] == 1) {
                    grid[x + 1][y] = 0;
                    q.offer(new int[]{x + 1, y});
                    path.append(level+1).append("D");
                }

                if (y - 1 >= 0 && grid[x][y - 1] == 1) {
                    grid[x][y - 1] = 0;
                    q.offer(new int[]{x, y - 1});
                    path.append(level+1).append("L");
                }

                if (y + 1 <= grid[0].length - 1 && grid[x][y + 1] == 1) {
                    grid[x][y + 1] = 0;
                    q.offer(new int[]{x, y + 1});
                    path.append(level+1).append("R");
                }
            }
            level++;
        }
        return path.toString().hashCode();
    }


