    // Time Complexity - O(row*column)
    // Space Complexity - O(1) since modifying the board[][] by doing XOR with 256 to create value >=256
    //                          which is more than any existing character value 0-255
    public boolean exist(char[][] board, String word) {
        if (word == null || word.isEmpty())
            return true;


        for (int row = 0; row < board.length; row++) {

            for (int col = 0; col < board[0].length; col++) {
                if (board[row][col] == word.charAt(0)) {

                    if (existUtil(board, row, col, word.toCharArray(), 0))
                        return true;
                }
            }
        }

        return false;
    }

    // DFS
    private boolean existUtil(char[][] board, int row, int col, char[] word, int index) {

        if (!isSafe(board, row, col) || index >= word.length) {
            return false;
        }

        if (board[row][col] != word[index]) {
            return false;
        }

        board[row][col] ^= 256;

        if (index == word.length - 1) {
            return true;
        }

        boolean result = existUtil(board, row, col - 1, word, index + 1)
                || existUtil(board, row, col + 1, word, index + 1)
                || existUtil(board, row - 1, col, word, index + 1)
                || existUtil(board, row + 1, col, word, index + 1);

        board[row][col] ^= 256;  // very important - backtracking
        return result;
    }

    private boolean isSafe(char[][] board, int row, int col) {
        if (row < 0 || row >= board.length || col < 0 || col >= board[0].length || board[row][col] >= 256)
            return false;

        return true;
    }
